"use client";

import { useEffect } from 'react';
import { usePathname } from 'next/navigation';

export default function AnalyticsTracker() {
    const pathname = usePathname();

    useEffect(() => {
        // Debounce or just fire on mount
        const track = async () => {
            try {
                await fetch('/api/analytics', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ path: pathname })
                });
            } catch (e) {
                // Fail silently
            }
        };

        track();
    }, [pathname]);

    return null;
}

"use client";

import { motion, useMotionValue, useSpring, useTransform } from 'framer-motion';
import { ArrowUpRight, Heart, Eye } from 'lucide-react';
import { ProjectType } from './Projects';
import { useState } from 'react';

function BentoCard({ project, index, showViewCounter, liked, onLike }: {
    project: ProjectType,
    index: number,
    showViewCounter: boolean,
    liked: boolean,
    onLike: (id: string, e: React.MouseEvent) => void
}) {
    const x = useMotionValue(0);
    const y = useMotionValue(0);

    const mouseXSpring = useSpring(x);
    const mouseYSpring = useSpring(y);

    const rotateX = useTransform(mouseYSpring, [-0.5, 0.5], ["10deg", "-10deg"]);
    const rotateY = useTransform(mouseXSpring, [-0.5, 0.5], ["-10deg", "10deg"]);

    const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
        const rect = e.currentTarget.getBoundingClientRect();
        const width = rect.width;
        const height = rect.height;
        const mouseX = e.clientX - rect.left;
        const mouseY = e.clientY - rect.top;
        const xPct = mouseX / width - 0.5;
        const yPct = mouseY / height - 0.5;
        x.set(xPct);
        y.set(yPct);
    };

    const handleMouseLeave = () => {
        x.set(0);
        y.set(0);
    };

    // Determine span based on index for a bento feel
    const isLarge = index === 0 || index === 5;
    const isTall = index === 2;

    return (
        <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ delay: index * 0.1 }}
            onMouseMove={handleMouseMove}
            onMouseLeave={handleMouseLeave}
            style={{
                rotateX,
                rotateY,
                transformStyle: "preserve-3d",
            }}
            className={`relative group rounded-3xl overflow-hidden border border-border/10 bg-surfaceHighlight/20 backdrop-blur-sm transition-colors hover:border-accent/40 ${isLarge ? 'md:col-span-2 md:row-span-1' : isTall ? 'md:row-span-2' : ''
                }`}
        >
            <div
                style={{ transform: "translateZ(50px)" }}
                className="absolute inset-4 z-20 flex flex-col justify-between pointer-events-none"
            >
                <div className="flex justify-between items-start">
                    <div className="flex gap-2">
                        {showViewCounter && project.views !== undefined && (
                            <div className="bg-black/20 backdrop-blur-md px-3 py-1 rounded-full flex items-center gap-1.5 text-white/80 text-[10px] font-bold border border-white/5">
                                <Eye size={12} />
                                <span>{project.views}</span>
                            </div>
                        )}
                        <button
                            onClick={(e) => project._id && onLike(project._id, e)}
                            className={`pointer-events-auto bg-black/20 backdrop-blur-md px-3 py-1 rounded-full flex items-center gap-1.5 text-[10px] font-bold border border-white/5 transition-all hover:scale-110 ${liked ? 'text-red-500' : 'text-white/80 hover:text-red-400'}`}
                        >
                            <Heart size={12} className={liked ? 'fill-current' : ''} />
                            {project.likes && <span>{project.likes}</span>}
                        </button>
                    </div>
                    <div className="p-2 bg-accent rounded-full opacity-0 group-hover:opacity-100 transition-all transform scale-50 group-hover:scale-100">
                        <ArrowUpRight size={16} className="text-white" />
                    </div>
                </div>

                <div>
                    <div className="flex flex-wrap gap-1.5 mb-2">
                        {project.tags.slice(0, 2).map(tag => (
                            <span key={tag} className="text-[9px] uppercase tracking-wider font-bold px-2 py-0.5 rounded-full bg-white/10 text-white/60 backdrop-blur-md">
                                {tag}
                            </span>
                        ))}
                    </div>
                    <h3 className="text-xl font-bold text-white mb-1 tracking-tight">{project.title}</h3>
                    <p className="text-white/50 text-xs line-clamp-2 max-w-[250px]">{project.description}</p>
                </div>
            </div>

            {/* Background Image with Parallax */}
            <motion.div
                className="absolute inset-0 z-10"
                style={{
                    backgroundImage: `url(${project.image})`,
                    backgroundSize: 'cover',
                    backgroundPosition: 'center',
                }}
            >
                <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-black/20 to-transparent" />
            </motion.div>

            <a
                href={project._id ? `/projects/${project._id}` : (project.link || '#')}
                className="absolute inset-0 z-30 pointer-events-auto"
            />
        </motion.div>
    );
}

export default function BentoGrid({ projects, showViewCounter, likedProjects, onLike }: {
    projects: ProjectType[],
    showViewCounter: boolean,
    likedProjects: string[],
    onLike: (id: string, e: React.MouseEvent) => void
}) {
    return (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6 auto-rows-[300px]">
            {projects.map((project, idx) => (
                <BentoCard
                    key={project._id || idx}
                    project={project}
                    index={idx}
                    showViewCounter={showViewCounter}
                    liked={likedProjects.includes(project._id || '')}
                    onLike={onLike}
                />
            ))}
        </div>
    );
}

"use client";

import { motion } from "framer-motion";
import { useEffect, useState, useRef } from "react";
import { useTheme } from "@/contexts/ThemeContext";

interface BioSkillTreeProps {
    skills: (string | { name: string; icon?: string })[];
}

interface HexNode {
    x: number;
    y: number;
    skill: { name: string; icon?: string };
    id: string;
}

export default function BioSkillTree({ skills }: BioSkillTreeProps) {
    const containerRef = useRef<HTMLDivElement>(null);
    const [nodes, setNodes] = useState<HexNode[]>([]);
    const [imgError, setImgError] = useState<Record<string, boolean>>({});
    const [containerHeight, setContainerHeight] = useState(650); // Default min height
    const [dimensions, setDimensions] = useState({ width: 0, height: 0 });
    const [clusterStyle, setClusterStyle] = useState({ width: 0, height: 0 });
    const [renderScale, setRenderScale] = useState(1);
    const { theme } = useTheme();

    const isMobile = dimensions.width < 768;

    // Handle Resize
    useEffect(() => {
        const handleResize = () => {
            if (containerRef.current) {
                const { width, height } = containerRef.current.getBoundingClientRect();
                setDimensions({ width, height });
            }
        };

        handleResize();
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    useEffect(() => {
        if (!dimensions.width || !skills.length) return;

        const { width, height } = dimensions;
        const isMobile = width < 768;

        // Configuration
        const HEX_SIZE = isMobile ? 50 : 65;
        const HEX_WIDTH = Math.sqrt(3) * HEX_SIZE;
        const HEX_HEIGHT = 2 * HEX_SIZE;

        // Visual Node Size (includes shadows/padding)
        const NODE_WIDTH = isMobile ? 90 : 130;
        const NODE_HEIGHT = isMobile ? 100 : 150;

        // Spacing with Gaps
        // Tight spacing for a dense honeycomb look
        const xGap = isMobile ? 4 : 4;
        const yGap = isMobile ? 4 : 4;

        // Center of the container
        // const centerX = width / 2;
        // const centerY = Math.max(height / 2, 350);

        // BFS Spiral Generator (Distance Sorted)
        // Ensures the cluster is always perfectly circular
        const getCircularCoords = (count: number) => {
            if (count === 0) return [];

            const center = { q: 0, r: 0 };
            const results = [center];
            const filled = new Set(['0,0']);

            // Initial neighbors
            const getNeighbors = (q: number, r: number) => [
                { q: q + 1, r: r - 1 }, { q: q + 1, r: r },
                { q: q, r: r + 1 }, { q: q - 1, r: r + 1 },
                { q: q - 1, r: r }, { q: q, r: r - 1 }
            ];

            let candidates = getNeighbors(0, 0);

            while (results.length < count) {
                // Sort candidates by distance from center (0,0)
                // Distance in Hex grid? Euclidean distance of the centerpoint is best for visual circularity
                candidates.sort((a, b) => {
                    // Convert hex to pixel offsets for accurate "visual" distance
                    // Just purely geometric approximation:
                    // x ~ q + r/2
                    // y ~ r
                    const getDistSq = (h: { q: number, r: number }) => {
                        const x = h.q + h.r / 2;
                        const y = h.r;
                        return x * x + y * y;
                    };
                    return getDistSq(a) - getDistSq(b);
                });

                // Pick the closest unique candidate
                let next = candidates.shift();
                while (next && filled.has(`${next.q},${next.r}`)) {
                    next = candidates.shift();
                }

                if (next) {
                    results.push(next);
                    filled.add(`${next.q},${next.r}`);

                    // Add new neighbors
                    const neighbors = getNeighbors(next.q, next.r);
                    neighbors.forEach(n => {
                        if (!filled.has(`${n.q},${n.r}`)) {
                            candidates.push(n);
                        }
                    });
                } else {
                    break; // Should not happen
                }
            }
            return results;
        };

        // Spiral Gen
        const spiralMap = getCircularCoords(skills.length);

        let minX = Infinity;
        let maxX = -Infinity;
        let minY = Infinity;
        let maxY = -Infinity;

        // Pass 1: Generate Raw Coords from Spiral
        const tempNodes = skills.map((skill, i) => {
            const { q, r } = spiralMap[i];

            const w = HEX_WIDTH + xGap;
            const h_step = (HEX_HEIGHT * 0.75) + yGap;

            const x = w * (q + r / 2);
            const y = h_step * r;

            return { x, y, skill: typeof skill === 'string' ? { name: skill } : skill, id: `hex-${i}` };
        });

        // Pass 2: Calculate Visual Bounding Box (Edges)
        // We look at the actual visual extent of the nodes, not just centers.
        let minLeft = Infinity;
        let maxRight = -Infinity;
        let minTop = Infinity;
        let maxBottom = -Infinity;

        tempNodes.forEach(node => {
            minLeft = Math.min(minLeft, node.x - NODE_WIDTH / 2);
            maxRight = Math.max(maxRight, node.x + NODE_WIDTH / 2);
            minTop = Math.min(minTop, node.y - NODE_HEIGHT / 2);
            maxBottom = Math.max(maxBottom, node.y + NODE_HEIGHT / 2);
        });

        // Calculate size and center
        const clusterW = maxRight - minLeft;
        const clusterH = maxBottom - minTop;
        const centerX = (minLeft + maxRight) / 2;
        const centerY = (minTop + maxBottom) / 2;

        setClusterStyle({ width: clusterW, height: clusterH });

        // Calculate Responsive Scale & Container Height
        const padding = isMobile ? 20 : 40;
        const availableWidth = Math.max(0, width - padding);
        const scale = clusterW > availableWidth ? availableWidth / clusterW : 1;
        setRenderScale(scale);

        // Dynamic Height
        const verticalPadding = isMobile ? 60 : 100;
        const finalHeight = (clusterH * scale) + verticalPadding;
        setContainerHeight(finalHeight);

        // Normalize Coordinates: Shift so Bounding Box Center becomes (0,0)
        const finalNodes = tempNodes.map(node => ({
            ...node,
            x: node.x - centerX,
            y: node.y - centerY
        }));

        setNodes(finalNodes);

    }, [skills, dimensions.width]);

    return (
        <div
            ref={containerRef}
            className="relative w-full overflow-hidden bg-white/5 backdrop-blur-sm rounded-3xl border border-white/10 shadow-inner transition-all duration-300 ease-in-out"
            style={{ height: containerHeight }}
        >
            {/* Background Mesh */}
            <div className={`absolute inset-0 opacity-10 pointer-events-none rounded-3xl overflow-hidden ${theme === 'dark' ? 'bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-emerald-900/40 via-transparent to-transparent' : ''}`} />

            {/* Absolute Centered Cluster Wrapper */}
            <div
                className="absolute top-1/2 left-1/2 transition-transform duration-300 ease-out origin-center"
                style={{
                    width: 0,
                    height: 0,
                    transform: `scale(${renderScale})`
                }}
            >
                {nodes.map((node, i) => {
                    const isMobile = dimensions.width < 768;
                    return (
                        <motion.div
                            key={node.id}
                            className="absolute"
                            style={{
                                left: node.x,
                                top: node.y,
                                transform: 'translate(-50%, -50%)',
                                width: isMobile ? 90 : 130,
                                height: isMobile ? 100 : 150,
                                zIndex: 10
                            }}
                            initial={{ scale: 0, opacity: 0 }}
                            whileInView={{ scale: 1, opacity: 1 }}
                            viewport={{ once: true }}
                            transition={{
                                duration: 0.5,
                                delay: i * 0.03,
                                type: "spring",
                                stiffness: 200,
                                damping: 15
                            }}
                            whileHover={{ scale: 1.15, zIndex: 50 }}
                        >
                            <div className="relative w-full h-full flex items-center justify-center group cursor-pointer">

                                {/* SVG Hexagon Background */}
                                <svg
                                    viewBox="0 0 100 100"
                                    className="absolute inset-0 w-full h-full drop-shadow-md"
                                    preserveAspectRatio="xMidYMid meet"
                                >
                                    <path
                                        d="M50 0 L93.3 25 V75 L50 100 L6.7 75 V25 Z"
                                        className={`transition-colors duration-300 stroke-1 
                                            ${theme === 'dark'
                                                ? 'fill-neutral-900/90 stroke-neutral-700 group-hover:fill-neutral-800'
                                                : 'fill-white/90 stroke-neutral-200 group-hover:fill-white'} 
                                        `}
                                    />
                                </svg>

                                {/* Content Layer */}
                                <div className="relative z-10 flex flex-col items-center justify-center gap-2 transform transition-transform group-hover:scale-105">
                                    {/* Icon */}
                                    <div className={`w-8 h-8 md:w-10 md:h-10 flex items-center justify-center rounded-full  ${theme === 'dark' ? 'bg-neutral-800' : 'bg-neutral-50'} p-1.5`}>
                                        {node.skill.icon && !imgError[node.id] ? (
                                            <img
                                                src={node.skill.icon}
                                                alt={node.skill.name}
                                                className="w-full h-full object-contain"
                                                onError={() => setImgError(prev => ({ ...prev, [node.id]: true }))}
                                            />
                                        ) : (
                                            <div className="w-full h-full rounded-full bg-emerald-500 shadow-[0_0_10px_#10b981]" />
                                        )}
                                    </div>

                                    {/* Text */}
                                    <span className={`text-[8px] md:text-[9px] uppercase tracking-widest font-bold text-center max-w-[80%] ${theme === 'dark' ? 'text-neutral-400' : 'text-neutral-500'}`}>
                                        {node.skill.name}
                                    </span>
                                </div>
                            </div>
                        </motion.div>
                    )
                })}
            </div>
        </div>
    );
}

"use client";

import { useState } from 'react';
import Link from 'next/link';
import { Search } from 'lucide-react';
import { Input, Card, Badge } from './ui';

export default function BlogList({ posts }: { posts: any[] }) {
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedTag, setSelectedTag] = useState<string | null>(null);

    // Extract unique tags
    const allTags = Array.from(new Set(posts.flatMap(post => post.tags || [])));

    // Filter posts
    const filteredPosts = posts.filter(post => {
        const matchesSearch = (post.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
            post.description?.toLowerCase().includes(searchTerm.toLowerCase()));
        const matchesTag = !selectedTag || post.tags?.includes(selectedTag);
        return matchesSearch && matchesTag;
    });

    return (
        <div className="max-w-4xl mx-auto">
            {/* Search Bar */}
            <div className="mb-8 relative">
                <Input
                    placeholder="Search articles..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-12"
                />
                <Search className="absolute left-4 top-1/2 -translate-y-1/2 text-secondary" size={20} />
            </div>

            {/* Tag Filter */}
            {allTags.length > 0 && (
                <div className="mb-8 flex flex-wrap gap-2">
                    <button
                        onClick={() => setSelectedTag(null)}
                        className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${!selectedTag
                            ? 'bg-accent text-black'
                            : 'bg-surface border border-border text-secondary hover:opacity-80'
                            }`}
                    >
                        All
                    </button>
                    {allTags.map(tag => (
                        <button
                            key={tag}
                            onClick={() => setSelectedTag(tag)}
                            className={`px-4 py-2 rounded-full text-sm font-medium transition-colors ${selectedTag === tag
                                ? 'bg-accent text-black'
                                : 'bg-surface border border-border text-secondary hover:opacity-80'
                                }`}
                        >
                            {tag}
                        </button>
                    ))}
                </div>
            )}

            {/* Posts Grid */}
            <div className="grid gap-12">
                {filteredPosts.length > 0 ? filteredPosts.map((post: any) => (
                    <Link href={`/blog/${post.slug}`} key={post._id} className="group block">
                        <Card className="hover:border-accent/40 transition-colors">
                            <div className="flex flex-col md:flex-row gap-6 md:items-center justify-between mb-4">
                                <div className="flex gap-2">
                                    {post.tags?.map((tag: string, i: number) => (
                                        <Badge key={i} variant="outline" className="text-accent border-accent/20">
                                            {tag}
                                        </Badge>
                                    ))}
                                </div>
                                <span>{Math.ceil((post.content?.split(' ').length || 0) / 200)} min read</span>
                            </div>

                            <p className="text-secondary leading-relaxed mb-6">
                                {post.description}
                            </p>

                            <span className="text-sm font-bold text-primary group-hover:underline decoration-accent underline-offset-4">
                                Read Article &rarr;
                            </span>
                        </Card>
                    </Link>
                )) : (
                    <div className="text-center py-24 border border-dashed border-border rounded-2xl">
                        <p className="text-secondary text-lg">No posts match your search.</p>
                    </div>
                )}
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from 'react';
import Link from 'next/link';
import Image from 'next/image';
import { ArrowLeft, List } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import { motion } from 'framer-motion';

export default function BlogPostContent({ post }: { post: any }) {
    const [headings, setHeadings] = useState<{ id: string; text: string; level: number }[]>([]);
    const [activeId, setActiveId] = useState<string>('');

    // Generate IDs for headings and extract them
    useEffect(() => {
        const elements = document.querySelectorAll('h2, h3');
        const items: { id: string; text: string; level: number }[] = [];

        elements.forEach((el) => {
            if (!el.id) {
                el.id = el.textContent?.toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '') || '';
            }
            if (el.id) {
                items.push({
                    id: el.id,
                    text: el.textContent || '',
                    level: parseInt(el.tagName.substring(1))
                });
            }
        });

        setHeadings(items);

        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        setActiveId(entry.target.id);
                    }
                });
            },
            { rootMargin: '-20% 0px -35% 0px' }
        );

        elements.forEach((el) => observer.observe(el));

        return () => observer.disconnect();
    }, [post.content]);

    // Custom renderer to add IDs to headings (redundant if checking DOM, but good for SSR/initial render consistency if extended)
    const components = {
        h2: ({ children }: any) => {
            const id = children?.toString().toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '');
            return <h2 id={id} className="scroll-mt-24">{children}</h2>;
        },
        h3: ({ children }: any) => {
            const id = children?.toString().toLowerCase().replace(/\s+/g, '-').replace(/[^\w-]/g, '');
            return <h3 id={id} className="scroll-mt-24">{children}</h3>;
        }
    };

    return (
        <main className="flex-grow py-12 px-6 relative">
            <div className="max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-[1fr_300px] gap-12">

                {/* Main Content */}
                <article className="lg:max-w-3xl w-full">
                    <Link
                        href="/blog"
                        className="inline-flex items-center text-sm text-secondary hover:text-white transition-colors mb-8 group"
                    >
                        <ArrowLeft size={16} className="mr-2 group-hover:-translate-x-1 transition-transform" />
                        Back to Blog
                    </Link>

                    <header className="mb-12">
                        <div className="flex gap-2 mb-6">
                            {post.tags.map((tag: string, i: number) => (
                                <span key={i} className="text-xs font-medium px-2 py-1 rounded-full bg-accent/10 text-accent border border-accent/20">
                                    {tag}
                                </span>
                            ))}
                        </div>
                        <h1 className="font-display text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6 leading-tight">
                            {post.title}
                        </h1>
                        <div className="flex items-center gap-4 text-sm text-secondary/60 font-mono border-l-2 border-accent pl-4">
                            <time>
                                {new Date(post.publishedAt).toLocaleDateString(undefined, { year: 'numeric', month: 'long', day: 'numeric' })}
                            </time>
                            <span></span>
                            <span>{Math.ceil(post.content.split(' ').length / 200)} min read</span>
                        </div>
                    </header>

                    {post.coverImage && (
                        <div className="relative aspect-video w-full rounded-2xl overflow-hidden border border-white/10 mb-12">
                            <Image
                                src={post.coverImage}
                                alt={post.title}
                                fill
                                className="object-cover"
                                priority
                            />
                        </div>
                    )}

                    <div className="prose prose-invert prose-lg max-w-none prose-headings:font-display prose-headings:font-bold prose-a:text-accent prose-img:rounded-xl prose-pre:bg-surfaceHighlight prose-pre:border prose-pre:border-white/10">
                        <ReactMarkdown components={components}>
                            {post.content}
                        </ReactMarkdown>
                    </div>
                </article>

                {/* Sidebar ToC */}
                <aside className="hidden lg:block relative">
                    <div className="sticky top-32">
                        <div className="bg-surface/50 backdrop-blur-sm border border-border rounded-2xl p-6">
                            <h4 className="font-bold text-white mb-4 flex items-center gap-2">
                                <List size={16} className="text-accent" />
                                On this page
                            </h4>
                            <nav className="flex flex-col space-y-1 max-h-[60vh] overflow-y-auto pr-2 scrollbar-thin scrollbar-thumb-white/10">
                                {headings.map((heading) => (
                                    <a
                                        key={heading.id}
                                        href={`#${heading.id}`}
                                        className={`text-sm transition-all duration-200 border-l-2 pl-4 py-1 block ${activeId === heading.id
                                                ? 'border-accent text-accent font-medium'
                                                : 'border-transparent text-secondary hover:text-white hover:border-white/20'
                                            } ${heading.level === 3 ? 'ml-4' : ''}`}
                                        onClick={(e) => {
                                            e.preventDefault();
                                            document.getElementById(heading.id)?.scrollIntoView({ behavior: 'smooth' });
                                            setActiveId(heading.id);
                                        }}
                                    >
                                        {heading.text}
                                    </a>
                                ))}
                                {headings.length === 0 && (
                                    <p className="text-secondary/40 text-sm italic">No sections detected</p>
                                )}
                            </nav>
                        </div>
                    </div>
                </aside>
            </div>
        </main>
    );
}

"use client";

import { useState, useRef, useEffect } from 'react';
import { MessageSquare, X, Send, Sparkles, Bot } from 'lucide-react';
import { AnimatePresence, motion } from 'framer-motion';

export default function ChatWidget() {
    const [isOpen, setIsOpen] = useState(false);
    const [messages, setMessages] = useState<{ role: 'user' | 'model'; text: string }[]>([
        { role: 'model', text: 'Hi there! I\'m your digital copilot. Ask me anything about my work, stacks, or availability.' }
    ]);
    const [input, setInput] = useState('');
    const [loading, setLoading] = useState(false);
    const messagesEndRef = useRef<HTMLDivElement>(null);

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages, isOpen]);

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!input.trim() || loading) return;

        const userMsg = input.trim();
        setInput('');
        setMessages(prev => [...prev, { role: 'user', text: userMsg }]);
        setLoading(true);

        try {
            const res = await fetch('/api/chat', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    message: userMsg,
                    history: messages.map(m => ({
                        role: m.role === 'user' ? 'user' : 'model',
                        parts: [{ text: m.text }]
                    }))
                })
            });

            const data = await res.json();

            if (data.error) {
                setMessages(prev => [...prev, { role: 'model', text: "Systems offline. Please reload and try again." }]);
            } else {
                setMessages(prev => [...prev, { role: 'model', text: data.reply }]);
            }

        } catch (error) {
            setMessages(prev => [...prev, { role: 'model', text: "Signal lost. Attempting to reconnect..." }]);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="fixed inset-0 z-[9999] pointer-events-none flex flex-col justify-end items-end p-4 md:p-6">
            <div className="pointer-events-auto">
                <AnimatePresence>
                    {isOpen && (
                        <motion.div
                            initial={{ opacity: 0, scale: 0.9, y: 20, x: 20 }}
                            animate={{ opacity: 1, scale: 1, y: 0, x: 0 }}
                            exit={{ opacity: 0, scale: 0.9, y: 20, x: 20 }}
                            className="mb-4 w-[85vw] max-w-[380px] h-[550px] max-h-[80vh] bg-surface/90 backdrop-blur-xl border border-white/10 rounded-3xl shadow-2xl flex flex-col overflow-hidden origin-bottom-right pointer-events-auto"
                        >
                            {/* Header */}
                            <div className="p-4 border-b border-border/50 bg-gradient-to-r from-accent/5 to-transparent flex justify-between items-center">
                                <div className="flex items-center gap-3">
                                    <div className="p-2 bg-accent/10 rounded-xl border border-accent/20">
                                        <Bot size={18} className="text-accent" />
                                    </div>
                                    <div>
                                        <h3 className="font-bold text-primary text-sm flex items-center gap-2">
                                            AI Assistant
                                            <span className="w-2 h-2 rounded-full bg-green-500 animate-pulse" />
                                        </h3>
                                        <div className="text-[10px] text-secondary font-mono uppercase tracking-wider">Online & Ready</div>
                                    </div>
                                </div>
                                <button
                                    onClick={() => setIsOpen(false)}
                                    className="p-2 hover:bg-white/5 rounded-full text-secondary hover:text-primary transition-colors"
                                    aria-label="Close chat"
                                >
                                    <X size={18} />
                                </button>
                            </div>

                            {/* Messages */}
                            <div className="flex-1 overflow-y-auto p-4 space-y-6 scrollbar-thin scrollbar-thumb-white/10 scrollbar-track-transparent">
                                {messages.map((msg, idx) => (
                                    <div key={idx} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
                                        <div className={`max-w-[80%] p-4 rounded-2xl text-sm leading-relaxed shadow-sm ${msg.role === 'user'
                                            ? 'bg-primary text-background rounded-br-sm'
                                            : 'bg-surfaceHighlight/50 border border-border/50 text-secondary rounded-bl-sm'
                                            }`}>
                                            {msg.text}
                                        </div>
                                    </div>
                                ))}
                                {loading && (
                                    <div className="flex justify-start">
                                        <div className="bg-surfaceHighlight/50 border border-border/50 p-4 rounded-2xl rounded-bl-sm">
                                            <div className="flex gap-1.5">
                                                <span className="w-1.5 h-1.5 bg-accent/50 rounded-full animate-bounce" />
                                                <span className="w-1.5 h-1.5 bg-accent/50 rounded-full animate-bounce delay-100" />
                                                <span className="w-1.5 h-1.5 bg-accent/50 rounded-full animate-bounce delay-200" />
                                            </div>
                                        </div>
                                    </div>
                                )}
                                <div ref={messagesEndRef} />
                            </div>

                            {/* Input */}
                            <form onSubmit={handleSubmit} className="p-4 border-t border-border/50 bg-surface/50">
                                <div className="relative group">
                                    <input
                                        value={input}
                                        onChange={e => setInput(e.target.value)}
                                        placeholder="Type your message..."
                                        className="w-full bg-surfaceHighlight/50 border border-border/50 rounded-2xl pl-4 pr-12 py-3.5 text-sm text-primary placeholder:text-secondary/40 focus:outline-none focus:border-accent/50 focus:bg-surfaceHighlight transition-all"
                                    />
                                    <button
                                        type="submit"
                                        disabled={!input.trim() || loading}
                                        className="absolute right-2 top-1/2 -translate-y-1/2 p-2 bg-accent text-background rounded-xl hover:scale-105 active:scale-95 transition-all disabled:opacity-50 disabled:hover:scale-100 disabled:grayscale"
                                    >
                                        <Send size={16} strokeWidth={2.5} />
                                    </button>
                                </div>
                                <div className="text-[10px] text-center text-secondary/30 mt-3 font-mono">
                                    Powered by Enhanced LLM Engine
                                </div>
                            </form>
                        </motion.div>
                    )}
                </AnimatePresence>

                <button
                    onClick={() => setIsOpen(!isOpen)}
                    className="group relative flex items-center justify-center w-14 h-14 md:w-16 md:h-16 bg-primary text-background rounded-full shadow-[0_10px_40px_-10px_rgba(0,0,0,0.3)] hover:shadow-[0_20px_40px_-10px_rgba(0,0,0,0.4)] transition-all hover:scale-110 active:scale-90 pointer-events-auto"
                    aria-label={isOpen ? "Close chat" : "Open chat"}
                >
                    <AnimatePresence mode='wait'>
                        {isOpen ? (
                            <motion.div
                                key="close"
                                initial={{ rotate: -90, opacity: 0 }}
                                animate={{ rotate: 0, opacity: 1 }}
                                exit={{ rotate: 90, opacity: 0 }}
                            >
                                <X size={24} />
                            </motion.div>
                        ) : (
                            <motion.div
                                key="open"
                                initial={{ scale: 0, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                exit={{ scale: 0, opacity: 0 }}
                                className="relative"
                            >
                                <Sparkles size={24} className="animate-pulse" />
                                <span className="absolute -top-1 -right-1 flex h-3 w-3">
                                    <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-accent opacity-75"></span>
                                    <span className="relative inline-flex rounded-full h-3 w-3 bg-accent"></span>
                                </span>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </button>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect } from 'react';
import { AnimatePresence } from 'framer-motion';
import Navbar from './Navbar';
import Hero from './Hero';
import About from './About';
import Projects, { ProjectType } from './Projects';
import Contact from './Contact';
import Footer from './Footer';
import Preloader from './Preloader';
import SmoothScroll from './SmoothScroll';
import { CommandPalette } from './CommandPalette';
import ChatWidget from './ChatWidget';
import AnalyticsTracker from './AnalyticsTracker';
import TechMarquee from './TechMarquee';
import ExperienceSection from '@/components/ExperienceSection';
import ProcessSection from './ProcessSection';
import LabsSection from './LabsSection';
import TestimonialsSection from './TestimonialsSection';
import NewsletterSection from './NewsletterSection';
import SpotifyTracker from './SpotifyTracker';
import GitHubPulse from './GitHubPulse';
import Pricing from './Pricing';

import Terminal from './Terminal';
import { useTerminal } from '@/hooks/useTerminal';

interface ClientHomeProps {
    projects: ProjectType[];
    settings?: any;
    experience?: any[];
    skills: any[];
}

export default function ClientHome({ projects, settings, experience, skills }: ClientHomeProps) {
    const [loading, setLoading] = useState(true);
    const [isTerminalOpen, setIsTerminalOpen] = useState(false);

    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            if (e.key === '`') {
                e.preventDefault();
                setIsTerminalOpen(prev => !prev);
            }
        };
        window.addEventListener('keydown', handleKeyDown);
        return () => window.removeEventListener('keydown', handleKeyDown);
    }, []);

    return (
        <>
            <AnimatePresence mode='wait'>
                {loading && <Preloader onComplete={() => setLoading(false)} />}
            </AnimatePresence>

            {!loading && (
                <div className="bg-background min-h-screen text-secondary font-sans selection:bg-accent selection:text-white">
                    <AnalyticsTracker />
                    <CommandPalette />
                    <SpotifyTracker settings={settings?.contact} featureEnabled={settings?.features?.spotifyTracker} />
                    <Terminal
                        isOpen={isTerminalOpen}
                        onClose={() => setIsTerminalOpen(false)}
                        projects={projects}
                        experience={experience || []}
                    />
                    {(!settings || settings.features?.aiChat !== false) && <ChatWidget />}
                    <Navbar settings={settings} />
                    <SmoothScroll>
                        {(!settings || settings.visibility?.hero !== false) && (
                            <>
                                <Hero settings={settings?.hero} />
                                {(!settings || settings.features?.threeDHero !== false) && <TechMarquee />}
                            </>
                        )}
                        {(!settings || settings.visibility?.projects !== false) && (
                            <>
                                <Projects projects={projects} showViewCounter={settings?.features?.viewCounter !== false} />
                                <LabsSection />
                                <GitHubPulse settings={settings?.contact} featureEnabled={settings?.features?.githubPulse} />
                            </>
                        )}
                        {(!settings || settings.visibility?.experience !== false) && (
                            <ExperienceSection experience={experience || []} />
                        )}
                        <ProcessSection />
                        {(!settings || settings.visibility?.about !== false) && (
                            <>
                                <About settings={settings?.about} skills={skills} />
                                <TestimonialsSection />
                                <Pricing />
                            </>
                        )}
                        <NewsletterSection />

                        {(!settings || settings.visibility?.contact !== false) && (
                            <>
                                <Contact settings={settings?.contact} />
                                <Footer settings={settings?.contact} visibility={settings?.visibility} projectCount={projects.length} />
                            </>
                        )}
                    </SmoothScroll>
                </div>
            )}
        </>
    );
}

