        const payload = {
            ...formData,
            tags: formData.tags.split(',').map((tag: string) => tag.trim())
        };

        try {
            const url = isEdit ? `/api/projects/${initialData._id}` : '/api/projects';
            const method = isEdit ? 'PUT' : 'POST';

            const res = await fetch(url, {
                method,
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(payload)
            });

            if (!res.ok) throw new Error('Failed to save');

            router.push('/admin/projects');
            router.refresh();
        } catch (error) {
            console.error(error);
            alert('Something went wrong');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div>
            <div className="mb-6">
                <Link href="/admin/projects" className="flex items-center text-sm text-secondary hover:text-primary transition-colors">
                    <ArrowLeft size={16} className="mr-2" /> Back to Projects
                </Link>
            </div>
            <h1 className="text-3xl font-display text-primary mb-8">{isEdit ? 'Edit Project' : 'New Project'}</h1>
            <form onSubmit={handleSubmit} className="space-y-6 max-w-2xl">
                <div>
                    <label className="block text-sm font-medium text-secondary mb-1">Title</label>
                    <input
                        name="title"
                        value={formData.title}
                        onChange={handleChange}
                        className="w-full bg-surfaceHighlight border border-border rounded p-2 text-primary focus:outline-none focus:border-accent"
                        required
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-secondary mb-1">Short Description</label>
                    <textarea
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        className="w-full bg-surfaceHighlight border border-border rounded p-2 text-primary focus:outline-none focus:border-accent"
                        rows={3}
                        required
                    />
                </div>
                <div>
                    <label className="block text-sm font-medium text-secondary mb-1">Tags (comma separated)</label>
                    <input
                        name="tags"
                        value={formData.tags}
                        onChange={handleChange}
                        className="w-full bg-surfaceHighlight border border-border rounded p-2 text-primary focus:outline-none focus:border-accent"
                        required
                    />
                </div>
                <ImageUploader
                    value={formData.image}
                    onChange={handleImageChange}
                    label="Project Image"
                    placeholder="Enter URL or upload an image"
                />
                <div>
                    <label className="block text-sm font-medium text-secondary mb-1">Project Link (Demo/GitHub)</label>
                    <input
                        name="link"
                        value={formData.link}
                        onChange={handleChange}
                        className="w-full bg-surfaceHighlight border border-border rounded p-2 text-primary focus:outline-none focus:border-accent"
                    />
                </div>

                <div className="pt-4 border-t border-border">
                    <div className="flex items-center gap-2 mb-2">
                        <h3 className="text-lg font-bold text-white">Case Study</h3>
                        <div className="group relative">
                            <Info size={16} className="text-secondary cursor-help" />
                            <div className="absolute left-6 top-0 w-64 bg-black border border-border p-2 rounded text-xs text-secondary hidden group-hover:block z-50">
                                Write a detailed case study using Markdown. This will be displayed on the individual project page.
                            </div>
                        </div>
                    </div>

                    <label className="block text-sm font-medium text-secondary mb-1">Content (Markdown)</label>
                    <textarea
                        name="content"
                        value={formData.content}
                        onChange={handleChange}
                        className="w-full bg-surfaceHighlight border border-border rounded p-2 text-primary focus:outline-none focus:border-accent font-mono text-sm"
                        rows={12}
                        placeholder="# Project Overview&#10;&#10;Here is a deep dive into the project..."
                    />
                </div>

                <button
                    type="submit"
                    disabled={loading}
                    className="px-6 py-2 bg-white text-black font-semibold rounded hover:bg-gray-200 transition-colors disabled:opacity-50"
                >
                    {loading ? 'Saving...' : 'Save Project'}
                </button>
            </form>
        </div>
    );
}

"use client";

import { useEffect, useState } from 'react';
import { motion, useSpring, useMotionValue } from 'framer-motion';

export default function MouseSpotlight() {
    const mouseX = useMotionValue(0);
    const mouseY = useMotionValue(0);

    const springConfig = { damping: 25, stiffness: 150 };
    const springX = useSpring(mouseX, springConfig);
    const springY = useSpring(mouseY, springConfig);

    useEffect(() => {
        const handleMouseMove = (e: MouseEvent) => {
            mouseX.set(e.clientX);
            mouseY.set(e.clientY);
        };

        window.addEventListener('mousemove', handleMouseMove);
        return () => window.removeEventListener('mousemove', handleMouseMove);
    }, [mouseX, mouseY]);

    return (
        <motion.div
            className="pointer-events-none fixed inset-0 z-30 transition-opacity duration-300"
            style={{
                background: `radial-gradient(600px circle at ${springX}px ${springY}px, var(--accent-spotlight), transparent 80%)`,
            }}
        />
    );
}

"use client";

import React, { createContext, useContext, useState, useEffect, useCallback, useRef } from 'react';

interface SoundContextType {
    muted: boolean;
    toggleMute: () => void;
    playHover: () => void;
    playClick: () => void;
    playSuccess: () => void;
    playError: () => void;
}

const SoundContext = createContext<SoundContextType | undefined>(undefined);

export function SoundProvider({ children }: { children: React.ReactNode }) {
    const [muted, setMuted] = useState(false);
    const audioContextRef = useRef<AudioContext | null>(null);
    const gainNodeRef = useRef<GainNode | null>(null);

    // Initialize AudioContext
    const initAudio = useCallback(() => {
        if (!audioContextRef.current) {
            const AudioContextClass = window.AudioContext || (window as any).webkitAudioContext;
            const ctx = new AudioContextClass();
            const gain = ctx.createGain();
            gain.connect(ctx.destination);
            gain.gain.value = 0.1; // Master volume (10%)

            audioContextRef.current = ctx;
            gainNodeRef.current = gain;
        } else if (audioContextRef.current.state === 'suspended') {
            audioContextRef.current.resume();
        }
    }, []);

    // Initialize on first user interaction
    useEffect(() => {
        const handleInteraction = () => {
            initAudio();
            // We can remove listeners after first successful init/resume
            if (audioContextRef.current && audioContextRef.current.state === 'running') {
                window.removeEventListener('click', handleInteraction);
                window.removeEventListener('keydown', handleInteraction);
                window.removeEventListener('touchstart', handleInteraction);
            }
        };

        window.addEventListener('click', handleInteraction);
        window.addEventListener('keydown', handleInteraction);
        window.addEventListener('touchstart', handleInteraction);

        return () => {
            window.removeEventListener('click', handleInteraction);
            window.removeEventListener('keydown', handleInteraction);
            window.removeEventListener('touchstart', handleInteraction);
        };
    }, [initAudio]);

    const toggleMute = () => {
        setMuted(prev => !prev);
    };

    // --- Sound Synthesis Engines ---

    const playHover = useCallback(() => {
        if (muted) return;
        // Try to resume if suspended (e.g. came back to tab)
        if (audioContextRef.current?.state === 'suspended') {
            audioContextRef.current.resume().catch(() => { });
        }

        const ctx = audioContextRef.current;
        const mainGain = gainNodeRef.current;

        if (!ctx || !mainGain) return;

        try {
            const osc = ctx.createOscillator();
            const gain = ctx.createGain();

            osc.type = 'sine';
            osc.frequency.setValueAtTime(400, ctx.currentTime);
            osc.frequency.exponentialRampToValueAtTime(600, ctx.currentTime + 0.05);

            gain.gain.setValueAtTime(0.05, ctx.currentTime);
            gain.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.05);

            osc.connect(gain);
            gain.connect(mainGain);

            osc.start();
            osc.stop(ctx.currentTime + 0.05);
        } catch (e) {
            console.error("Audio playback error", e);
        }
    }, [muted]);

    const playClick = useCallback(() => {
        if (muted) return;
        // Ensure context is running
        if (!audioContextRef.current) {
            initAudio();
        } else if (audioContextRef.current.state === 'suspended') {
            audioContextRef.current.resume().catch(() => { });
        }

        const ctx = audioContextRef.current;
        const mainGain = gainNodeRef.current;

        if (!ctx || !mainGain) return;

        try {
            const osc = ctx.createOscillator();
            const gain = ctx.createGain();

            osc.type = 'square';
            osc.frequency.setValueAtTime(200, ctx.currentTime);
            osc.frequency.exponentialRampToValueAtTime(100, ctx.currentTime + 0.1);

            gain.gain.setValueAtTime(0.05, ctx.currentTime);
            gain.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.1);

            osc.connect(gain);
            gain.connect(mainGain);

            osc.start();
            osc.stop(ctx.currentTime + 0.1);
        } catch (e) {
            console.error("Audio playback error", e);
        }
    }, [muted, initAudio]);

    const playSuccess = useCallback(() => {
        if (muted) return;
        const ctx = audioContextRef.current;
        const mainGain = gainNodeRef.current;

        if (!ctx || !mainGain) return;

        const now = ctx.currentTime;

        // Arpeggio
        [440, 554, 659].forEach((freq, i) => {
            const osc = ctx.createOscillator();
            const gain = ctx.createGain();

            osc.type = 'sine';
            osc.frequency.value = freq;

            gain.gain.setValueAtTime(0.05, now + i * 0.05);
            gain.gain.exponentialRampToValueAtTime(0.001, now + i * 0.05 + 0.2);

            osc.connect(gain);
            gain.connect(mainGain);

            osc.start(now + i * 0.05);
            osc.stop(now + i * 0.05 + 0.2);
        });
    }, [muted]);

    const playError = useCallback(() => {
        if (muted) return;
        const ctx = audioContextRef.current;
        const mainGain = gainNodeRef.current;

        if (!ctx || !mainGain) return;

        const osc = ctx.createOscillator();
        const gain = ctx.createGain();

        osc.type = 'sawtooth';
        osc.frequency.setValueAtTime(150, ctx.currentTime);
        osc.frequency.linearRampToValueAtTime(100, ctx.currentTime + 0.2);

        gain.gain.setValueAtTime(0.05, ctx.currentTime);
        gain.gain.exponentialRampToValueAtTime(0.001, ctx.currentTime + 0.2);

        osc.connect(gain);
        gain.connect(mainGain);

        osc.start();
        osc.stop(ctx.currentTime + 0.2);
    }, [muted]);

    return (
        <SoundContext.Provider value={{ muted, toggleMute, playHover, playClick, playSuccess, playError }}>
            {children}
        </SoundContext.Provider>
    );
}

export function useSound() {
    const context = useContext(SoundContext);
    if (!context) {
        throw new Error('useSound must be used within a SoundProvider');
    }
    return context;
}

"use client";

import { useState, useCallback } from 'react';

export type TerminalLine = {
    type: 'input' | 'output' | 'error';
    content: string;
};

export function useTerminal(projects: any[] = [], experience: any[] = []) {
    const [history, setHistory] = useState<TerminalLine[]>([
        { type: 'output', content: 'Welcome to MRS-OS v1.0.0' },
        { type: 'output', content: 'Type "help" for a list of available commands.' }
    ]);

    const processCommand = useCallback((cmd: string) => {
        const trimmedCmd = cmd.trim().toLowerCase();
        setHistory(prev => [...prev, { type: 'input', content: cmd }]);

        switch (trimmedCmd) {
            case 'help':
                setHistory(prev => [...prev, {
                    type: 'output',
                    content: 'Available commands: whoami, projects, experience, clear, exit, help'
                }]);
                break;
            case 'whoami':
                setHistory(prev => [...prev, {
                    type: 'output',
                    content: 'A Full Stack Developer & Creative Technologist focused on building high-end digital experiences.'
                }]);
                break;
            case 'projects':
                if (projects.length === 0) {
                    setHistory(prev => [...prev, { type: 'output', content: 'No projects found.' }]);
                } else {
                    projects.forEach(p => {
                        setHistory(prev => [...prev, { type: 'output', content: `> ${p.title} - ${p.description.substring(0, 50)}...` }]);
                    });
                }
                break;
            case 'experience':
                if (experience.length === 0) {
                    setHistory(prev => [...prev, { type: 'output', content: 'No experience data found.' }]);
                } else {
                    experience.forEach(exp => {
                        setHistory(prev => [...prev, { type: 'output', content: `> ${exp.role} @ ${exp.company} (${exp.startDate} - ${exp.endDate || 'Present'})` }]);
                    });
                }
                break;
            case 'clear':
                setHistory([]);
                break;
            default:
                if (trimmedCmd !== '') {
                    setHistory(prev => [...prev, { type: 'error', content: `Command not found: ${trimmedCmd}` }]);
                }
                break;
        }
    }, [projects, experience]);

    return { history, processCommand };
}

import mongoose from 'mongoose';

/**
 * Global is used here to maintain a cached connection across hot reloads
 * in development. This prevents connections growing exponentially
 * during API Route usage.
 */
let cached = (global as any).mongoose;

if (!cached) {
    cached = (global as any).mongoose = { conn: null, promise: null };
}

async function connectToDatabase() {
    const MONGODB_URI = process.env.MONGODB_URI;

    if (!MONGODB_URI) {
        throw new Error(
            'Please define the MONGODB_URI environment variable inside .env.local'
        );
    }

    if (cached.conn) {
        return cached.conn;
    }

    if (!cached.promise) {
        const opts = {
            bufferCommands: false,
        };

        cached.promise = mongoose.connect(MONGODB_URI, opts).then((mongoose) => {
            return mongoose;
        });
    }
    try {
        cached.conn = await cached.promise;
    } catch (e) {
        cached.promise = null;
        throw e;
    }

    return cached.conn;
}

export default connectToDatabase;
